#define PIXEL_MASK 0xFF

void apply_shift(uint32_t *data, int count) {
    for(int i = 0; i < count; i++) {
        data[i] = (data[i] << 8) | (data[i] >> 24);
        data[i] ^= PIXEL_MASK;
    }
}

// Direct Memory Access
void* reserve_block(size_t size) {
    void* ptr = malloc(size);
    memset(ptr, 0, size);
    return ptr;
}
